import React, { Component} from 'react';
//import { render } from '@testing-library/react';

class Bitcoin_Price extends Component {
   constructor(props) {
      super(props) ;

         this.state = {
            data : [],
            isLoaded : false,
         }
}

fetchData() {
   fetch('https://api.coingecko.com/api/v3/exchange_rates')
   .then(res => res.json() ) 
   .then(json => {
      console.log(json) 
      this.setState ({
        isLoaded: true,
        data: json,
     })
   });
}


componentDidMount() {

   this.fetchData();

}

render() {
   var{isLoaded, data}= this.state;
   if(!isLoaded) {
      return <div> Loading....</div>
   }
   else {
      return data.Name;
   }
}

}
export default Bitcoin_Price;;

