import React, { Component} from 'react';
//import { render } from '@testing-library/react';

class ApiCall extends Component {
   constructor(props) {
      super(props) ;

         this.state = {
            items : [ ],
            isLoaded : false,
         }
}
componentDidMount() {
    fetch('172.22.134.242:8081')
    .then(res => res.text ) 
    .then(text => {
      this.setState ({
         isLoaded: true,
         items: text,
      })
   });
}

render() {
   var{isLoaded,items}= this.state;
   if(!isLoaded) {
      return <div> Loading....</div>
   }
   else {
      return (
         <div className='App'> 
            <ul>
               {items}
            </ul>
         </div>
      );
   }
}

}
export default ApiCall;;

